import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import Login from './Login';
import Register from './Register';
import ForgotPassword from './ForgotPassword';
import TabViewData from './TabViewData';
import JobOffers from './JobOffers';
import Dashboard from './Dashboard';
import { getUser } from './auth';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function App() {
  const user = getUser();

  return (
    <Router>
      {user ? (
        <>
          <nav style={{ padding: '10px', backgroundColor: '#f0f0f0' }}>
            <ul style={{ display: 'flex', listStyle: 'none', gap: '20px', margin: 0, padding: 0 }}>
              <li>
                <Link to="/">Wpisy</Link>
              </li>
              <li>
                <Link to="/oferty-pracy">Oferty Pracy</Link>
              </li>
            </ul>
          </nav>
          <Routes>
            <Route path="/" element={<TabViewData />} />
            <Route path="/oferty-pracy" element={<JobOffers />} />
          </Routes>
        </>
      ) : (
        <Routes>
          <Route path="/register" element={<Register />} />
          <Route path="/forgot-password" element={<ForgotPassword />} />
          <Route path="/login" element={<Login />} />
          {/* Jeśli użytkownik nie jest zalogowany, przekierowujemy wszystkie trasy na /login */}
          <Route path="*" element={<Navigate to="/login" />} />
        </Routes>
      )}
      <ToastContainer position="top-right" autoClose={3000} />
    </Router>
  );
}

export default App;
