import { useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';

function TabInputData() {
  const [form, setForm] = useState({
    imie: '',
    nazwisko: '',
    jezyk: '',
    fs: '',
    nr: '',
    do_opieki: '',
    dyspozycyjnosc: '',
    oczekiwania: '',
    referencje: '',
    ostatni_kontakt: '',
    notatka: ''
  });

  const handleChange = (field, value) => {
    setForm({ ...form, [field]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3001/add-entry', form);
      toast.success('Dane zapisane!');
      setForm({
        imie: '', nazwisko: '', jezyk: '', fs: '', nr: '',
        do_opieki: '', dyspozycyjnosc: '', oczekiwania: '',
        referencje: '', ostatni_kontakt: '', notatka: ''
      });
    } catch (err) {
      toast.error('Błąd zapisu danych');
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      background: '#f0f4ff',
      padding: 20
    }}>
      <div style={{
        width: '100%',
        maxWidth: '600px',
        background: '#fff',
        padding: '30px',
        borderRadius: '12px',
        boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
        boxSizing: 'border-box'
      }}>
        <h2 style={{ textAlign: 'center', marginBottom: 20 }}>Wprowadzanie danych</h2>
        <form onSubmit={handleSubmit}>
          {[
            { label: 'Imię', key: 'imie' },
            { label: 'Nazwisko', key: 'nazwisko' },
            { label: 'Poziom języka niemieckiego', key: 'jezyk' },
            { label: 'FS', key: 'fs' },
            { label: 'NR', key: 'nr' },
            { label: 'Do opieki', key: 'do_opieki' },
            { label: 'Dyspozycyjność', key: 'dyspozycyjnosc' },
            { label: 'Oczekiwania', key: 'oczekiwania' },
            { label: 'Referencje', key: 'referencje' },
            { label: 'Ostatni kontakt', key: 'ostatni_kontakt', type: 'date' },
            { label: 'Notatka', key: 'notatka', type: 'textarea' },
          ].map(({ label, key, type }) => (
            <div key={key} style={{ marginBottom: 12 }}>
              <label style={{ fontWeight: 500 }}>{label}</label><br />
              {type === 'textarea' ? (
                <textarea
                  value={form[key]}
                  onChange={e => handleChange(key, e.target.value)}
                  rows={3}
                  style={{
                    width: '100%',
                    padding: '10px',
                    borderRadius: '8px',
                    border: '1px solid #ccc',
                    boxSizing: 'border-box'
                  }}
                />
              ) : (
                <input
                  type={type || 'text'}
                  value={form[key]}
                  onChange={e => handleChange(key, e.target.value)}
                  style={{
                    width: '100%',
                    padding: '10px',
                    borderRadius: '8px',
                    border: '1px solid #ccc',
                    boxSizing: 'border-box'
                  }}
                />
              )}
            </div>
          ))}

          <button
            type="submit"
            style={{
              width: '100%',
              padding: '12px',
              backgroundColor: '#007bff',
              color: '#fff',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer',
              marginTop: 10
            }}
          >
            Zapisz dane
          </button>
        </form>
      </div>
    </div>
  );
}

export default TabInputData;
