import { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import { toast } from 'react-toastify';
import TabInputData from './TabInputData'; // Komponent do dodawania nowych danych

Modal.setAppElement('#root');  // Ustawienie aplikacji jako root dla modala

const tdStyle = {
  padding: '10px',
  verticalAlign: 'top',
  borderBottom: '1px solid #eee',
  fontSize: '14px'
};

const deleteBtn = {
  backgroundColor: '#dc3545',
  color: '#fff',
  border: 'none',
  borderRadius: '6px',
  padding: '4px 8px',
  cursor: 'pointer'
};

const labelStyle = {
  display: 'block',
  textAlign: 'left',
  fontWeight: 600,
  marginBottom: 4,
  marginLeft: 3
};

const inputStyle = {
  width: '100%',
  padding: '10px',
  borderRadius: '8px',
  border: '1px solid #ccc',
  boxSizing: 'border-box',
  outlineColor: '#007bff'
};

// Formatowanie miesiąca
const formatMonthYear = (monthYearStr) => {
  if (!monthYearStr || monthYearStr.length !== 7 || !monthYearStr.includes('-')) return ''; // Sprawdzamy, czy format jest poprawny

  const [year, month] = monthYearStr.split('-'); // Rozdzielamy rok i miesiąc
  if (!month || !year || isNaN(month) || isNaN(year)) return ''; // Jeśli miesiąc lub rok są nieprawidłowe, zwróć pusty string
  
  const date = new Date(year, month - 1); // Tworzymy obiekt Date (miesiąc - 1, bo w JavaScript miesiące zaczynają się od 0)

  // Tablica miesięcy w języku polskim
  const months = [
    'styczeń', 'luty', 'marzec', 'kwiecień', 'maj', 'czerwiec',
    'lipiec', 'sierpień', 'wrzesień', 'październik', 'listopad', 'grudzień'
  ];

  const formattedMonth = months[date.getMonth()]; // Pobieramy nazwę miesiąca
  const formattedYear = date.getFullYear(); // Pobieramy rok

  return `${formattedMonth} ${formattedYear}`; // Zwracamy w formacie "Mmmm YYYY"
};

// Konwertowanie formatu "Mmmm YYYY" do "YYYY-MM"
const convertToMonthYearFormat = (monthYearStr) => {
  if (!monthYearStr) return '';
  const months = {
    'styczeń': '01', 'luty': '02', 'marzec': '03', 'kwiecień': '04', 'maj': '05', 'czerwiec': '06',
    'lipiec': '07', 'sierpień': '08', 'wrzesień': '09', 'październik': '10', 'listopad': '11', 'grudzień': '12'
  };

  const [monthName, year] = monthYearStr.split(' ');
  const month = months[monthName.toLowerCase()] || '01'; // Zmieniamy nazwę miesiąca na numer
  return `${year}-${month}`; // Zwracamy format "YYYY-MM"
};

function TabViewData() {
  const [entries, setEntries] = useState([]);  // Lista danych
  const [sortedEntries, setSortedEntries] = useState([]);  // Posortowane dane
  const [editingEntry, setEditingEntry] = useState(null);  // Stan edytowanego wpisu
  const [editForm, setEditForm] = useState({});  // Formularz edycji
  const [isAdding, setIsAdding] = useState(false);  // Widoczność modala dodawania
  const [sortOrder, setSortOrder] = useState('asc');  // Kierunek sortowania (rosnąco/malejąco)
  const [sortColumn, setSortColumn] = useState('nazwisko');  // Kolumna do sortowania
  const [searchQuery, setSearchQuery] = useState('');  // Stan zapytania do wyszukiwania
  const [isSearchVisible, setIsSearchVisible] = useState(false);  // Stan widoczności pola wyszukiwania

  const formatDate = (dateStr) => {
    if (!dateStr) return ''; // Jeśli brak daty, zwróć pusty string
    return dateStr.slice(0, 10);  // Wyciąga tylko 'YYYY-MM-DD'
  };

  // Normalizowanie danych przed zapisaniem
  const normalizeEntryData = (entry) => {
    return {
      ...entry,
      imie: entry.imie, // Normalize imie
      nazwisko: entry.nazwisko, // Normalize nazwisko
      jezyk: entry.jezyk, // Normalize jezyk
      fs: entry.fs,
      nr: entry.nr,
      do_opieki: entry.do_opieki,
      dyspozycyjnosc: entry.dyspozycyjnosc,
      oczekiwania: entry.oczekiwania,
      referencje: entry.referencje,
      ostatni_kontakt: entry.ostatni_kontakt,
      notatka: entry.notatka
    }
  };

  // Funkcja do filtrowania wpisów na podstawie wyszukiwania
  const filterEntries = (entries) => {
    if (!searchQuery) return entries; // Jeśli nie ma zapytania, zwróć wszystkie wpisy
    return entries.filter(entry => {
      const searchString = `${entry.imie} ${entry.nazwisko} ${entry.jezyk} ${entry.fs} ${entry.nr} ${entry.do_opieki} ${entry.dyspozycyjnosc} ${entry.oczekiwania} ${entry.referencje} ${entry.ostatni_kontakt} ${entry.notatka}`.toLowerCase();
      return searchString.includes(searchQuery.toLowerCase());
    });
  };

  const parseToMonth = (dateStr) => {
    if (!dateStr) return '';
    const [day, month, year] = dateStr.split('-');
    return `${year}-${month}`;
  };

  // Pobieranie danych z serwera
  const fetchEntries = () => {
    axios.get('/entries')
      .then(res => {
        const normalizedData = res.data.map(normalizeEntryData);
        setEntries(normalizedData);
        setSortedEntries(normalizedData);  // Domyślnie wszystkie dane
      })
      .catch(error => console.error('Błąd pobierania danych:', error));
  };

  useEffect(() => {
    fetchEntries();  // Pobieramy dane po załadowaniu komponentu
  }, []);

  // Funkcja obsługująca zmianę wartości w polu wyszukiwania
  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSort = (column) => {
    let newSortOrder = 'asc';  // Domyślnie ustawiamy sortowanie rosnące

    // Zmiana kierunku sortowania po kliknięciu w tę samą kolumnę
    if (sortColumn === column) {
      newSortOrder = sortOrder === 'asc' ? 'desc' : 'asc'; // Jeśli klikamy na tę samą kolumnę, zmieniamy kierunek
    }

    const columnKey = column
      .toLowerCase()
      .replace(/\s+/g, '_') // Zastąp wszystkie spacje podkreślnikami
      .normalize("NFD") // Użyj formatu rozdzielającego znaki (usuwanie diakrytyków)
      .replace(/[\u0300-\u036f]/g, '') // Usuń znaki diakrytyczne (np. ę, ó itp.)
      .replace(/[ąćęłńóśżź]/g, (match) => match.normalize('NFD').replace(/[\u0300-\u036f]/g, '').toLowerCase()); // Usuwamy dodatkowe znaki, takie jak ą, ć itd.

    const sortedData = [...entries].sort((a, b) => {
      let valueA = a[columnKey];
      let valueB = b[columnKey];

      if (valueA === undefined || valueB === undefined) {
        return 0;
      }

      if (!isNaN(valueA) && !isNaN(valueB)) {
        valueA = parseFloat(valueA);
        valueB = parseFloat(valueB);
      }

      if (typeof valueA === 'string' && typeof valueB === 'string') {
        return newSortOrder === 'asc' 
          ? valueA.localeCompare(valueB, 'pl', { sensitivity: 'base' })
          : valueB.localeCompare(valueA, 'pl', { sensitivity: 'base' });
      }

      return newSortOrder === 'asc' ? valueA - valueB : valueB - valueA;
    });

    setSortedEntries(sortedData);
    setSortOrder(newSortOrder); // Zmiana kierunku sortowania
    setSortColumn(column); // Ustawienie kolumny do sortowania
  };

  // Funkcja do usuwania wpisu
  const handleDelete = async (id, e) => {
    e.stopPropagation();  // Zapobieganie wywołaniu funkcji edycji po kliknięciu kosza
    if (!window.confirm('Czy na pewno chcesz usunąć ten wpis?')) return;
    try {
      await axios.delete(`/entries/${id}`);
      toast.success('Wpis usunięty');
      fetchEntries();  // Odświeżenie listy wpisów
    } catch {
      toast.error('Błąd podczas usuwania');
    }
  };

  // Funkcja otwierania formularza edycji
  const handleEdit = (entry) => {
    setEditingEntry(entry);
    setEditForm({
      ...entry,
      dyspozycyjnosc: entry.dyspozycyjnosc ? entry.dyspozycyjnosc : '', // Upewniamy się, że dyspozycyjnosc jest pusta, jeśli brak wartości
      ostatni_kontakt: formatDate(entry.ostatni_kontakt),
    });
  };

  // Funkcja do zmiany danych w formularzu edycji
  const handleEditChange = (field, value) => {
    if (field === 'dyspozycyjnosc') {
      value = convertToMonthYearFormat(value); // Zmieniamy format daty w polu "dyspozycyjność"
    }
    setEditForm((prev) => ({ ...prev, [field]: value }));
  };

  // Funkcja do zapisania edytowanego wpisu
  const handleSaveEdit = async () => {
    try {
      await axios.put(`/entries/${editingEntry.id}`, editForm);
      toast.success('Zaktualizowano dane');
      setEditingEntry(null);  // Zamknięcie formularza po zapisaniu
      fetchEntries();  // Odświeżenie danych
    } catch {
      toast.error('Błąd edycji');
    }
  };

  return (
    <div style={{ overflowX: 'auto', padding: 20 }}>
      <h2 style={{ textAlign: 'center' }}>📄 Wpisy w bazie danych</h2>

      {/* Przycisk do otwierania pola wyszukiwania */}
      <button
        onClick={() => setIsSearchVisible(prev => !prev)}
        style={{
          padding: '10px 20px',
          backgroundColor: '#007bff',
          color: '#fff',
          border: 'none',
          borderRadius: '6px',
          cursor: 'pointer',
          fontSize: '16px',
          marginBottom: '20px'
        }}
      >
        {isSearchVisible ? 'Ukryj wyszukiwanie' : 'Pokaż wyszukiwanie'}
      </button>

      {/* Pole wyszukiwania - widoczne po kliknięciu */}
      {isSearchVisible && (
        <div style={{ marginBottom: '20px' }}>
          <input
            type="text"
            placeholder="Wyszukaj..."
            value={searchQuery}
            onChange={handleSearchChange}
            style={{
              padding: '10px',
              width: '100%',
              borderRadius: '8px',
              border: '1px solid #ccc',
              boxSizing: 'border-box',
              outlineColor: '#007bff'
            }}
          />
        </div>
      )}

      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead style={{ backgroundColor: '#007bff' }}>
          <tr>
            {['#', 'Imię', 'Nazwisko', 'Język', 'FS', 'NR', 'Do opieki', 'Dyspozycyjność', 'Oczekiwania', 'Referencje', 'Ostatni kontakt', 'Notatka'].map((col, i) => {
              const columnKey = col.toLowerCase().replace(' ', '_');
              return (
                <th
                  key={i}
                  style={{
                    padding: '10px',
                    color: '#fff',
                    fontWeight: '600',
                    textAlign: 'left',
                    borderBottom: '1px solid #e0e0e0',
                    cursor: 'pointer'
                  }}
                  onClick={() => handleSort(columnKey)}
                >
                  {col}
                  {sortColumn === columnKey && (sortOrder === 'asc' ? ' 🔼' : ' 🔽')}
                </th>
              );
            })}
            <th style={{ padding: '10px', textAlign: 'center', cursor: 'pointer' }} onClick={() => setIsAdding(true)}>
              <button
                style={{
                  padding: '5px 10px',
                  backgroundColor: '#28a745',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '20px',
                }}
              >
                +
              </button>
            </th>
          </tr>
        </thead>
        <tbody>
          {filterEntries(sortedEntries).map((entry, index) => (
            <tr
              key={entry.id}
              style={{
                borderBottom: '1px solid #eee',
                transition: 'background-color 0.2s',
                cursor: 'pointer',
              }}
              onClick={() => handleEdit(entry)}
            >
              <td>{index + 1}</td>
              <td>{entry.imie}</td>
              <td>{entry.nazwisko}</td>
              <td>{entry.jezyk}</td>
              <td>{entry.fs}</td>
              <td>{entry.nr}</td>
              <td>{entry.do_opieki}</td>
              <td>{entry.dyspozycyjnosc}</td>
              <td>{entry.oczekiwania}</td>
              <td>{entry.referencje}</td>
              {entry.ostatni_kontakt ? formatDate(entry.ostatni_kontakt) : '—'}
              <td>{entry.notatka}</td>
              <td>
                <button onClick={(e) => handleDelete(entry.id, e)} style={deleteBtn}>🗑️</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal do edytowania wpisu */}
      <Modal
        isOpen={!!editingEntry}
        onRequestClose={() => setEditingEntry(null)}
        contentLabel="Edytuj wpis"
        style={{
          content: {
            maxWidth: '600px',
            margin: 'auto',
            padding: '30px',
            borderRadius: '12px',
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
            background: '#fff'
          }
        }}
      >
        <button
          onClick={() => setEditingEntry(null)}
          style={{
            position: 'absolute',
            top: 10,
            right: 15,
            background: 'transparent',
            border: 'none',
            fontSize: 20,
            fontWeight: 'bold',
            cursor: 'pointer',
            color: '#999'
          }}
        >
          &times;
        </button>

        <h2 style={{ textAlign: 'center', marginBottom: 20 }}>✏️ Edycja wpisu</h2>
        {editingEntry && (
          <form onSubmit={(e) => { e.preventDefault(); handleSaveEdit(); }}>
            {/* Formularz edycji */}
            <div style={{ display: 'flex', gap: '10px', marginBottom: 16 }}>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>Imię</label>
                <input type="text" value={editForm.imie || ''} onChange={e => handleEditChange('imie', e.target.value)} style={inputStyle} />
              </div>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>Nazwisko</label>
                <input type="text" value={editForm.nazwisko || ''} onChange={e => handleEditChange('nazwisko', e.target.value)} style={inputStyle} />
              </div>
            </div>
            {/* Język + FS + NR */}
            <div style={{ display: 'flex', gap: '10px', marginBottom: 16 }}>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>Język</label>
                <select value={editForm.jezyk || ''} onChange={e => handleEditChange('jezyk', e.target.value)} style={inputStyle}>
                  <option value="">Wybierz</option>
                  {['A0', 'A1', 'A2', 'B1', 'B2', 'C1'].map(lvl => <option key={lvl} value={lvl}>{lvl}</option>)}
                </select>
              </div>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>FS</label>
                <select value={editForm.fs || ''} onChange={e => handleEditChange('fs', e.target.value)} style={inputStyle}>
                  <option value="">Wybierz</option>
                  <option value="Tak">Tak</option>
                  <option value="Nie">Nie</option>
                </select>
              </div>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>NR</label>
                <select value={editForm.nr || ''} onChange={e => handleEditChange('nr', e.target.value)} style={inputStyle}>
                  <option value="">Wybierz</option>
                  <option value="Tak">Tak</option>
                  <option value="Nie">Nie</option>
                </select>
              </div>
            </div>

            {/* Do opieki + Dyspozycyjność */}
            <div style={{ display: 'flex', gap: '10px', marginBottom: 16 }}>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>Do opieki</label>
                <select value={editForm.do_opieki || ''} onChange={e => handleEditChange('do_opieki', e.target.value)} style={inputStyle}>
                  <option value="">Wybierz</option>
                  <option value="senior">Senior</option>
                  <option value="seniorka">Seniorka</option>
                  <option value="małżeństwo">Małżeństwo</option>
                </select>
              </div>
              <div style={{ flex: 1 }}>
              <label style={labelStyle}>Dyspozycyjność</label>
              <input
  type="month"
  value={parseToMonth(editForm.dyspozycyjnosc)}
                onChange={e => handleEditChange('dyspozycyjnosc', e.target.value)}
                style={inputStyle}
              />
            </div>
            </div>

            {/* Oczekiwania */}
            <div style={{ marginBottom: 16 }}>
              <label style={labelStyle}>Oczekiwania</label>
              <textarea value={editForm.oczekiwania || ''} onChange={e => handleEditChange('oczekiwania', e.target.value)} rows={3} style={inputStyle} />
            </div>

            {/* Referencje + ostatni kontakt */}
            <div style={{ display: 'flex', gap: '10px', marginBottom: 16 }}>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>Referencje</label>
                <select value={editForm.referencje || ''} onChange={e => handleEditChange('referencje', e.target.value)} style={inputStyle}>
                  <option value="">Wybierz</option>
                  <option value="Tak">Tak</option>
                  <option value="Nie">Nie</option>
                </select>
              </div>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>Ostatni kontakt</label>
                <input type="date" value={editForm.ostatni_kontakt || ''} onChange={e => handleEditChange('ostatni_kontakt', e.target.value)} style={inputStyle} />
              </div>
            </div>

            {/* Notatka */}
            <div style={{ marginBottom: 16 }}>
              <label style={{ display: 'block', textAlign: 'center', fontWeight: 600, marginBottom: 4 }}>Notatka</label>
              <textarea value={editForm.notatka || ''} onChange={e => handleEditChange('notatka', e.target.value)} rows={3} style={inputStyle} />
            </div>
            <button type="submit" style={{
              width: '100%',
              padding: '12px',
              backgroundColor: '#007bff',
              color: '#fff',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer'
            }}>
              💾 Zapisz zmiany
            </button>
          </form>
        )}
      </Modal>

      {/* Modal do dodawania nowych danych */}
      <Modal
        isOpen={isAdding}
        onRequestClose={() => setIsAdding(false)}
        contentLabel="Dodaj nowy wpis"
        style={{
          content: {
            maxWidth: '600px',
            margin: 'auto',
            padding: '30px',
            borderRadius: '12px',
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
            background: '#fff'
          }
        }}
      >
        <button
          onClick={() => setIsAdding(false)}  // Zamknięcie modala
          style={{
            position: 'absolute',
            top: 10,
            right: 15,
            background: 'transparent',
            border: 'none',
            fontSize: 20,
            fontWeight: 'bold',
            cursor: 'pointer',
            color: '#999'
          }}
        >
          &times;
        </button>
        <h2 style={{ textAlign: 'center', marginBottom: 20 }}>+ Dodaj nowy wpis</h2>
        <TabInputData setIsAdding={setIsAdding} fetchEntries={fetchEntries} />
      </Modal>
    </div>
  );
}

export default TabViewData;
