import { useEffect, useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import Modal from 'react-modal';

Modal.setAppElement('#root'); // unikamy ostrzeżeń

function TabViewData() {
  const [entries, setEntries] = useState([]);
const [editingEntry, setEditingEntry] = useState(null);
const [editForm, setEditForm] = useState({});


  useEffect(() => {
    axios.get('/entries')
      .then(res => setEntries(res.data))
      .catch(() => console.error('Błąd pobierania danych'));
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Czy na pewno chcesz usunąć ten wpis?')) return;
    try {
      await axios.delete(`/entries/${id}`);
      toast.success('Wpis usunięty');
      setEntries(prev => prev.filter(entry => entry.id !== id));
    } catch {
      toast.error('Błąd usuwania wpisu');
    }
  };

  const handleEdit = (entry) => {
    setEditingEntry(entry);
    setEditForm({ ...entry });
  };
  
  const handleEditChange = (field, value) => {
    setEditForm((prev) => ({ ...prev, [field]: value }));
  };
  
  const handleSaveEdit = async () => {
    try {
      await axios.put(`/entries/${editingEntry.id}`, editForm);
      toast.success('Zaktualizowano dane');
      setEditingEntry(null);
      fetchEntries(); // lub odświeżenie danych
    } catch (err) {
      toast.error('Błąd zapisu');
    }
  };
  
  const fetchEntries = () => {
    axios.get('/entries').then(res => setEntries(res.data));
  };
  
  useEffect(() => {
    fetchEntries();
  }, []);
  

  return (
    <div style={{ overflowX: 'auto' }}>
      <h2 style={{ textAlign: 'center' }}>📄 Wpisy w bazie danych</h2>
      <table style={{
        width: '100%',
        borderCollapse: 'collapse',
        marginTop: 20,
        fontSize: '14px'
      }}>
        <thead>
          <tr style={{ backgroundColor: '#007bff', color: '#fff' }}>
            <th style={cell}>ID</th>
            <th style={cell}>Imię</th>
            <th style={cell}>Nazwisko</th>
            <th style={cell}>Język</th>
            <th style={cell}>FS</th>
            <th style={cell}>NR</th>
            <th style={cell}>Do opieki</th>
            <th style={cell}>Dyspozycyjność</th>
            <th style={cell}>Oczekiwania</th>
            <th style={cell}>Referencje</th>
            <th style={cell}>Ostatni kontakt</th>
            <th style={cell}>Notatka</th>
            <th style={cell}>Edycja</th>
            <th style={cell}>Usuń</th>
          </tr>
        </thead>
        <tbody>
        {entries.map((entry, index) => (
            <tr key={entry.id}>
             <td style={cell}>{index + 1}</td>
              <td style={cell}>{entry.imie}</td>
              <td style={cell}>{entry.nazwisko}</td>
              <td style={cell}>{entry.jezyk}</td>
              <td style={cell}>{entry.fs}</td>
              <td style={cell}>{entry.nr}</td>
              <td style={cell}>{entry.do_opieki}</td>
              <td style={cell}>{entry.dyspozycyjnosc}</td>
              <td style={cell}>{entry.oczekiwania}</td>
              <td style={cell}>{entry.referencje}</td>
              <td style={cell}>
  {entry.ostatni_kontakt
    ? new Date(entry.ostatni_kontakt).toLocaleDateString('pl-PL')
    : '—'}
</td>
              <td style={cell}>{entry.notatka}</td>
              <td style={cell}>
  <button
    onClick={() => handleEdit(entry)}
    style={{
      color: '#000',
      border: 'none',
      borderRadius: '6px',
      padding: '4px 8px',
      cursor: 'pointer'
    }}
  >
    ✏️
  </button>
</td>
              <td style={cell}>
  <button
    onClick={() => handleDelete(entry.id)}
    style={{
      color: '#fff',
      border: 'none',
      borderRadius: '6px',
      padding: '4px 8px',
      cursor: 'pointer'
    }}
  >
    🗑️
  </button>
</td>
            </tr>
          ))}
        </tbody>
      </table>
      <Modal
  isOpen={!!editingEntry}
  onRequestClose={() => setEditingEntry(null)}
  contentLabel="Edytuj wpis"
  style={{
    content: {
      maxWidth: '600px',
      margin: 'auto',
      borderRadius: '12px',
      padding: '20px'
    }
  }}
>
  <h2>Edytuj dane</h2>
  {editingEntry && (
    <form onSubmit={(e) => { e.preventDefault(); handleSaveEdit(); }}>
      {[
        { key: 'imie', label: 'Imię' },
        { key: 'nazwisko', label: 'Nazwisko' },
        { key: 'jezyk', label: 'Poziom języka' },
        { key: 'fs', label: 'FS' },
        { key: 'nr', label: 'NR' },
        { key: 'do_opieki', label: 'Do opieki' },
        { key: 'dyspozycyjnosc', label: 'Dyspozycyjność' },
        { key: 'oczekiwania', label: 'Oczekiwania' },
        { key: 'referencje', label: 'Referencje' },
        { key: 'ostatni_kontakt', label: 'Ostatni kontakt', type: 'date' },
        { key: 'notatka', label: 'Notatka' }
      ].map(({ key, label, type }) => (
        <div key={key} style={{ marginBottom: 12 }}>
          <label>{label}</label>
          {type === 'date' ? (
            <input
              type="date"
              value={editForm[key] || ''}
              onChange={(e) => handleEditChange(key, e.target.value)}
              style={{ width: '100%', padding: 8 }}
            />
          ) : (
            <input
              type="text"
              value={editForm[key] || ''}
              onChange={(e) => handleEditChange(key, e.target.value)}
              style={{ width: '100%', padding: 8 }}
            />
          )}
        </div>
      ))}

      <button type="submit" style={{ marginTop: 10, padding: '10px 20px' }}>
        💾 Zapisz zmiany
      </button>
    </form>
  )}
</Modal>
    </div>
  );
}

const cell = {
  padding: '8px',
  border: '1px solid #ddd',
  textAlign: 'left',
  verticalAlign: 'top'
};

export default TabViewData;
