// Offers.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const OffersTable = () => {
  const [fullOffers, setFullOffers] = useState([]); // wszystkie oferty
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  // Stan paginacji (klient-side)
  const [currentPage, setCurrentPage] = useState(1);
  const pageSize = 10; // liczba ofert na stronę

  useEffect(() => {
    const fetchAllOffers = async () => {
      setLoading(true);
      try {
        // Pobieramy wszystkie oferty - ustawiamy pageSize na wartość wystarczająco dużą, np. 100,
        // by otrzymać wszystkie (zakładamy, że meta.all wynosi 21).
        const response = await axios.get('https://api-lockstep.berlin-opiekunki.pl/v1/offer/all', {
          params: { pageSize: 100 }
        });
        console.log('Odpowiedź API:', response.data);
        // Oczekujemy struktury: { meta: { all: X, count: Y }, offers: [...] }
        const offersArray = response.data.offers || [];
        // Sortujemy wszystkie oferty według daty rozpoczęcia (rosnąco)
        const sortedOffers = offersArray.sort((a, b) => new Date(a.startDate) - new Date(b.startDate));
        setFullOffers(sortedOffers);
      } catch (err) {
        console.error('Błąd pobierania ofert:', err);
        setError('Błąd pobierania ofert');
      } finally {
        setLoading(false);
      }
    };

    fetchAllOffers();
  }, []);

  // Paginacja po stronie klienta
  const totalOffers = fullOffers.length;
  const totalPages = Math.ceil(totalOffers / pageSize);
  const currentOffers = fullOffers.slice((currentPage - 1) * pageSize, currentPage * pageSize);

  const handlePrevPage = () => {
    if (currentPage > 1) setCurrentPage(prev => prev - 1);
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) setCurrentPage(prev => prev + 1);
  };

  if (loading) return <div>Ładowanie ofert...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h2 style={{ textAlign: "center" }}>Oferty Pracy</h2>
      <table style={{ width: "100%", borderCollapse: "collapse", marginTop: "20px" }}>
        <thead>
          <tr style={{ backgroundColor: "#f4f4f4" }}>
            <th style={{ border: "1px solid #ddd", padding: "12px", textAlign: "left" }}>ID</th>
            <th style={{ border: "1px solid #ddd", padding: "12px", textAlign: "left" }}>Tytuł</th>
            <th style={{ border: "1px solid #ddd", padding: "12px", textAlign: "left" }}>Opis</th>
            <th style={{ border: "1px solid #ddd", padding: "12px", textAlign: "left" }}>Miasto</th>
            <th style={{ border: "1px solid #ddd", padding: "12px", textAlign: "left" }}>Kod</th>
            <th style={{ border: "1px solid #ddd", padding: "12px", textAlign: "left" }}>Płeć</th>
            <th style={{ border: "1px solid #ddd", padding: "12px", textAlign: "left" }}>Data rozpoczęcia</th>
          </tr>
        </thead>
        <tbody>
          {currentOffers.map((offer, index) => (
            <tr key={offer.id || index}>
              <td style={{ border: "1px solid #ddd", padding: "12px" }}>{offer.id}</td>
              <td style={{ border: "1px solid #ddd", padding: "12px" }}>{offer.title}</td>
              <td style={{ border: "1px solid #ddd", padding: "12px" }}>{offer.description}</td>
              <td style={{ border: "1px solid #ddd", padding: "12px" }}>{offer.city}</td>
              <td style={{ border: "1px solid #ddd", padding: "12px" }}>{offer.code}</td>
              <td style={{ border: "1px solid #ddd", padding: "12px" }}>{offer.sex}</td>
              <td style={{ border: "1px solid #ddd", padding: "12px" }}>
                {offer.startDate ? new Date(offer.startDate).toLocaleString() : 'Brak daty'}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div style={{ marginTop: "20px", display: "flex", justifyContent: "center", alignItems: "center" }}>
        <button onClick={handlePrevPage} disabled={currentPage === 1} style={{ marginRight: "10px" }}>
          Poprzednia strona
        </button>
        <span>Strona {currentPage} z {totalPages}</span>
        <button onClick={handleNextPage} disabled={currentPage === totalPages} style={{ marginLeft: "10px" }}>
          Następna strona
        </button>
      </div>
    </div>
  );
};

export default OffersTable;
