import { useEffect, useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import Modal from 'react-modal';

Modal.setAppElement('#root'); // unikamy ostrze≈ºe≈Ñ

function TabViewData() {
  const [entries, setEntries] = useState([]);
const [editingEntry, setEditingEntry] = useState(null);
const [editForm, setEditForm] = useState({});


  useEffect(() => {
    axios.get('/entries')
      .then(res => setEntries(res.data))
      .catch(() => console.error('B≈ÇƒÖd pobierania danych'));
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Czy na pewno chcesz usunƒÖƒá ten wpis?')) return;
    try {
      await axios.delete(`/entries/${id}`);
      toast.success('Wpis usuniƒôty');
      setEntries(prev => prev.filter(entry => entry.id !== id));
    } catch {
      toast.error('B≈ÇƒÖd usuwania wpisu');
    }
  };

  const handleEdit = (entry) => {
    setEditingEntry(entry);
    setEditForm({ ...entry });
  };
  
  const handleEditChange = (field, value) => {
    setEditForm((prev) => ({ ...prev, [field]: value }));
  };
  
  const handleSaveEdit = async () => {
    try {
      await axios.put(`/entries/${editingEntry.id}`, editForm);
      toast.success('Zaktualizowano dane');
      setEditingEntry(null);
      fetchEntries(); // lub od≈õwie≈ºenie danych
    } catch (err) {
      toast.error('B≈ÇƒÖd zapisu');
    }
  };
  
  const fetchEntries = () => {
    axios.get('/entries').then(res => setEntries(res.data));
  };
  
  useEffect(() => {
    fetchEntries();
  }, []);
  

  return (
    <div style={{ overflowX: 'auto' }}>
      <h2 style={{ textAlign: 'center' }}>üìÑ Wpisy w bazie danych</h2>
      <table style={{
        width: '100%',
        borderCollapse: 'collapse',
        marginTop: 20,
        fontSize: '14px'
      }}>
        <thead>
          <tr style={{ backgroundColor: '#007bff', color: '#fff' }}>
            <th style={cell}>ID</th>
            <th style={cell}>Imiƒô</th>
            <th style={cell}>Nazwisko</th>
            <th style={cell}>Jƒôzyk</th>
            <th style={cell}>FS</th>
            <th style={cell}>NR</th>
            <th style={cell}>Do opieki</th>
            <th style={cell}>Dyspozycyjno≈õƒá</th>
            <th style={cell}>Oczekiwania</th>
            <th style={cell}>Referencje</th>
            <th style={cell}>Ostatni kontakt</th>
            <th style={cell}>Notatka</th>
            <th style={cell}>Edytuj</th>
            <th style={cell}>Usu≈Ñ</th>
          </tr>
        </thead>
        <tbody>
          {entries.map(entry => (
            <tr key={entry.id}>
              <td style={cell}>{entry.id}</td>
              <td style={cell}>{entry.imie}</td>
              <td style={cell}>{entry.nazwisko}</td>
              <td style={cell}>{entry.jezyk}</td>
              <td style={cell}>{entry.fs}</td>
              <td style={cell}>{entry.nr}</td>
              <td style={cell}>{entry.do_opieki}</td>
              <td style={cell}>{entry.dyspozycyjnosc}</td>
              <td style={cell}>{entry.oczekiwania}</td>
              <td style={cell}>{entry.referencje}</td>
              <td style={cell}>
  {entry.ostatni_kontakt
    ? new Date(entry.ostatni_kontakt).toLocaleDateString('pl-PL')
    : '‚Äî'}
</td>
              <td style={cell}>{entry.notatka}</td>
              <td style={cell}>
  <button
    onClick={() => handleEdit(entry)}
    style={{
      color: '#000',
      border: 'none',
      borderRadius: '6px',
      padding: '4px 8px',
      cursor: 'pointer'
    }}
  >
    ‚úèÔ∏è
  </button>
</td>
              <td style={cell}>
  <button
    onClick={() => handleDelete(entry.id)}
    style={{
      color: '#fff',
      border: 'none',
      borderRadius: '6px',
      padding: '4px 8px',
      cursor: 'pointer'
    }}
  >
    üóëÔ∏è
  </button>
</td>
            </tr>
          ))}
        </tbody>
      </table>
      
    </div>
  );
}

const cell = {
  padding: '8px',
  border: '1px solid #ddd',
  textAlign: 'left',
  verticalAlign: 'top'
};

export default TabViewData;
