import { getUser, removeToken } from './auth';
import AdminPanel from './AdminPanel';
import UserPanel from './UserPanel';
import ChangePassword from './ChangePassword';
import { useState } from 'react';
import TabInputData from './TabInputData';
import TabViewData from './TabViewData';

function Dashboard() {
  const user = getUser();
  const [activeTab, setActiveTab] = useState('input');
  const [isAdding, setIsAdding] = useState(false);  // Stan do kontrolowania widoczno≈õci formularza
  const [editingEntry, setEditingEntry] = useState(null);  // Przechowuje dane edytowanego wpisu
  const [entries, setEntries] = useState([]);  // Przechowuje listƒô wpis√≥w
  return (
    <div style={{
      minHeight: '100vh',
      padding: 40,
      background: '#f0f4ff',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center'
    }}>
      <div style={{
        maxWidth: 600,
        width: '100%',
        background: '#fff',
        borderRadius: 12,
        padding: 30,
        boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
      }}>
        <button onClick={() => {
          removeToken();
          window.location.href = '/login';
        }}>Wyloguj</button>

        <h2 style={{ textAlign: 'center' }}>
          Witaj, {user?.role === 'admin' ? 'Administratorze' : 'U≈ºytkowniku'}!
        </h2>

        <div style={{ padding: 20 }}>
      <div style={{ marginBottom: 20 }}>
        <button onClick={() => setActiveTab('input')}>‚ûï Wprowadzanie danych</button>
        <button onClick={() => setActiveTab('view')}>üìÑ PodglƒÖd bazy</button>
        <button onClick={() => setActiveTab('password')}>üîê Zmiana has≈Ça</button>
      </div>
      {activeTab === 'input' && <TabInputData />}
{activeTab === 'view' && <TabViewData />}
{activeTab === 'password' && <ChangePassword />}
    </div>

        {user?.role === 'admin' ? <AdminPanel /> : <UserPanel />}
      </div>
    </div>
  );
}

export default Dashboard;
