import { useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';

function TabInputData() {
  const [form, setForm] = useState({
    imie: '',
    nazwisko: '',
    jezyk: '',
    fs: '',
    nr: '',
    do_opieki: '',
    dyspozycyjnosc: '',
    oczekiwania: '',
    referencje: '',
    ostatni_kontakt: '',
    notatka: ''
  });

  const handleChange = (field, value) => {
    setForm({ ...form, [field]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3001/add-entry', form);
      toast.success('Dane zapisane!');
      setForm({
        imie: '', nazwisko: '', jezyk: '', fs: '', nr: '',
        do_opieki: '', dyspozycyjnosc: '', oczekiwania: '',
        referencje: '', ostatni_kontakt: '', notatka: ''
      });
    } catch (err) {
      toast.error('Błąd zapisu danych');
    }
  };
  const inputStyle = {
  width: '100%',
  padding: '10px',
  borderRadius: '8px',
  border: '1px solid #ccc',
  boxSizing: 'border-box',
  outlineColor: '#007bff'
};

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      background: '#f0f4ff',
      padding: 20
    }}>
      <div style={{
        width: '100%',
        maxWidth: '600px',
        background: '#fff',
        padding: '30px',
        borderRadius: '12px',
        boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
        boxSizing: 'border-box'
      }}>
        <h2 style={{ textAlign: 'center', marginBottom: 20 }}>Wprowadzanie danych</h2>
        <form onSubmit={handleSubmit}>
{[
    { label: 'Imię', key: 'imie', placeholder: 'Wpisz imię' },
    { label: 'Nazwisko', key: 'nazwisko', placeholder: 'Wpisz nazwisko' },
    { label: 'Poziom języka niemieckiego', key: 'jezyk', placeholder: 'np. komunikatywny' },
    { label: 'FS', key: 'fs', placeholder: 'FS' },
    { label: 'NR', key: 'nr', placeholder: 'NR' },
    { label: 'Do opieki', key: 'do_opieki', placeholder: 'np. starsza pani' },
    { label: 'Dyspozycyjność', key: 'dyspozycyjnosc', placeholder: 'np. od zaraz' },
    { label: 'Oczekiwania', key: 'oczekiwania', placeholder: 'np. 1800€ netto' },
    { label: 'Referencje', key: 'referencje', placeholder: 'np. 2 miesiące DE' },
    { label: 'Ostatni kontakt', key: 'ostatni_kontakt', type: 'date' },
    { label: 'Notatka', key: 'notatka', type: 'textarea', placeholder: 'Dodatkowe informacje...' },
  ].map(({ label, key, type, placeholder }) => (
    <div key={key} style={{ marginBottom: 16 }}>
      <label style={{ display: 'block', textAlign: 'center', fontWeight: 600, marginBottom: 4 }}>
        {label}
      </label>
      {type === 'textarea' ? (
        <textarea
          placeholder={placeholder}
          value={form[key]}
          onChange={e => handleChange(key, e.target.value)}
          rows={3}
          style={{
            width: '100%',
            padding: '10px',
            borderRadius: '8px',
            border: '1px solid #ccc',
            boxSizing: 'border-box',
            outlineColor: '#007bff'
          }}
        />
      ) : (
        <input
          type={type || 'text'}
          placeholder={placeholder}
          value={form[key]}
          onChange={e => handleChange(key, e.target.value)}
          style={{
            width: '100%',
            padding: '10px',
            borderRadius: '8px',
            border: '1px solid #ccc',
            boxSizing: 'border-box',
            outlineColor: '#007bff'
          }}
        />
      )}
    </div>
  ))}

          <button
            type="submit"
            style={{
              width: '100%',
              padding: '12px',
              backgroundColor: '#007bff',
              color: '#fff',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer',
              marginTop: 10
            }}
          >
            Zapisz dane
          </button>
        </form>
      </div>
    </div>
  );
}

export default TabInputData;
