import React from "react";

const OffersTable = ({ offers }) => {
  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1 style={{ textAlign: "center" }}>Oferty Pracy</h1>
      <table
        style={{
          width: "100%",
          borderCollapse: "collapse",
          marginTop: "20px",
        }}
      >
        <thead>
          <tr style={{ backgroundColor: "#f4f4f4" }}>
            <th style={{ border: "1px solid #ddd", padding: "12px", textAlign: "left" }}>ID</th>
            <th style={{ border: "1px solid #ddd", padding: "12px", textAlign: "left" }}>Tytuł</th>
            <th style={{ border: "1px solid #ddd", padding: "12px", textAlign: "left" }}>Opis</th>
          </tr>
        </thead>
        <tbody>
          {offers.map((offer) => (
            <tr key={offer.id}>
              <td style={{ border: "1px solid #ddd", padding: "12px" }}>{offer.id}</td>
              <td style={{ border: "1px solid #ddd", padding: "12px" }}>{offer.title}</td>
              <td style={{ border: "1px solid #ddd", padding: "12px" }}>{offer.description}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export async function getServerSideProps() {
  try {
    // Pobranie danych z API (dostosuj adres, jeśli potrzebne)
    const res = await fetch("http://localhost:3000/api/lockstep-offers");
    const offers = await res.json();
    return {
      props: { offers },
    };
  } catch (error) {
    console.error("Błąd przy pobieraniu ofert:", error);
    return {
      props: { offers: [] },
    };
  }
}

export default OffersTable;
