import { useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';

function TabInputData() {
  const [form, setForm] = useState({
    imie: '',
    nazwisko: '',
    jezyk: '',
    fs: '',
    nr: '',
    do_opieki: '',
    dyspozycyjnosc: '',
    oczekiwania: '',
    referencje: '',
    ostatni_kontakt: '',
    notatka: ''
  });

  const handleChange = (field, value) => {
    setForm({ ...form, [field]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3001/add-entry', form);
      toast.success('Dane zapisane!');
      setForm({ // reset
        imie: '',
        nazwisko: '',
        jezyk: '',
        fs: '',
        nr: '',
        do_opieki: '',
        dyspozycyjnosc: '',
        oczekiwania: '',
        referencje: '',
        ostatni_kontakt: '',
        notatka: ''
      });
    } catch (err) {
      toast.error('Błąd zapisu danych');
      console.error(err);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 600 }}>
      {[
        { label: 'Imię', key: 'imie' },
        { label: 'Nazwisko', key: 'nazwisko' },
        { label: 'Poziom języka niemieckiego', key: 'jezyk' },
        { label: 'FS', key: 'fs' },
        { label: 'NR', key: 'nr' },
        { label: 'Do opieki', key: 'do_opieki' },
        { label: 'Dyspozycyjność', key: 'dyspozycyjnosc' },
        { label: 'Oczekiwania', key: 'oczekiwania' },
        { label: 'Referencje', key: 'referencje' },
        { label: 'Ostatni kontakt', key: 'ostatni_kontakt', type: 'date' },
        { label: 'Notatka', key: 'notatka', type: 'textarea' },
      ].map(({ label, key, type }) => (
        <div key={key} style={{ marginBottom: 10 }}>
          <label>{label}</label><br />
          {type === 'textarea' ? (
            <textarea
              value={form[key]}
              onChange={(e) => handleChange(key, e.target.value)}
              rows={3}
              style={{ width: '100%', padding: 8, borderRadius: 6 }}
            />
          ) : (
            <input
              type={type || 'text'}
              value={form[key]}
              onChange={(e) => handleChange(key, e.target.value)}
              style={{ width: '100%', padding: 8, borderRadius: 6 }}
            />
          )}
        </div>
      ))}

      <button
        type="submit"
        style={{
          padding: '10px 20px',
          backgroundColor: '#007bff',
          color: '#fff',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer'
        }}
      >
        Zapisz dane
      </button>
    </form>
  );
}

export default TabInputData;
