                  <option value="senior">Senior</option>
                  <option value="seniorka">Seniorka</option>
                  <option value="maÅ‚Å¼eÅ„stwo">MaÅ‚Å¼eÅ„stwo</option>
                </select>
              </div>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>DyspozycyjnoÅ›Ä‡</label>
                <input
                  type="month"
                  value={editForm.dyspozycyjnosc || ''}
                  onChange={e => handleEditChange('dyspozycyjnosc', e.target.value)}
                  style={inputStyle}
                />
              </div>
            </div>
            <div style={{ marginBottom: 16 }}>
              <label style={labelStyle}>Oczekiwania</label>
              <textarea value={editForm.oczekiwania || ''} onChange={e => handleEditChange('oczekiwania', e.target.value)} rows={3} style={inputStyle} />
            </div>
            <div style={{ display: 'flex', gap: '10px', marginBottom: 16 }}>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>Referencje</label>
                <select value={editForm.referencje || ''} onChange={e => handleEditChange('referencje', e.target.value)} style={inputStyle}>
                  <option value="">Wybierz</option>
                  <option value="Tak">Tak</option>
                  <option value="Nie">Nie</option>
                </select>
              </div>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>Ostatni kontakt</label>
                <input type="date" value={editForm.ostatni_kontakt || ''} onChange={e => handleEditChange('ostatni_kontakt', e.target.value)} style={inputStyle} />
              </div>
            </div>
            <div style={{ marginBottom: 16 }}>
              <label style={{ display: 'block', textAlign: 'center', fontWeight: 600, marginBottom: 4 }}>Notatka</label>
              <textarea value={editForm.notatka || ''} onChange={e => handleEditChange('notatka', e.target.value)} rows={3} style={inputStyle} />
            </div>
            {/* Przycisk i modal z ofertami pracy wedÅ‚ug dyspozycyjnoÅ›ci */}
            <div style={{ textAlign: 'center', marginBottom: '20px' }}>
              <button
                type="button"
                onClick={() => setIsOffersModalOpen(true)}
                style={{
                  padding: '10px 20px',
                  backgroundColor: '#28a745',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '16px'
                }}
              >
                PokaÅ¼ oferty pracy dla dyspozycyjnoÅ›ci {availability}
              </button>
              <EditOffersModal
                isOpen={isOffersModalOpen}
                onRequestClose={() => setIsOffersModalOpen(false)}
                availability={availability}
              />
            </div>
            <button type="submit" style={{
              width: '100%',
              padding: '12px',
              backgroundColor: '#007bff',
              color: '#fff',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer'
            }}>
              ðŸ’¾ Zapisz zmiany
            </button>
          </form>
        )}
      </Modal>
      <Modal
        isOpen={isAdding}
        onRequestClose={() => setIsAdding(false)}
        contentLabel="Dodaj nowy wpis"
        style={{
          content: {
            width: '800px',
            maxWidth: '80%',
            maxHeight: '90vh',
            overflowY: 'auto',
            padding: '30px',
            borderRadius: '12px',
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
            background: '#ffffff', // biaÅ‚y kolor tÅ‚a modala
            inset: '0',
            margin: 'auto',
            position: 'relative',
            zIndex: '1001'
          },
          overlay: {
            backgroundColor: 'rgba(0, 0, 0, 0.5)', // tÅ‚o caÅ‚ego ekranu za modalem
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            position: 'fixed',
            top: '0',
            left: '0',
            right: '0',
            bottom: '0',
            zIndex: '1000'
          }
        }}
      >
        <button
          onClick={() => setIsAdding(false)}
          style={{
            position: 'absolute',
            top: 10,
            right: 15,
            background: 'transparent',
            border: 'none',
            fontSize: 20,
            fontWeight: 'bold',
            cursor: 'pointer',
            color: '#999'
          }}
        >
          &times;
        </button>
        <h2 style={{ textAlign: 'center', marginBottom: 20 }}>+ Dodaj nowy wpis</h2>
        <TabInputData setIsAdding={setIsAdding} fetchEntries={fetchEntries} />
      </Modal>
    </div>
  );
}

