import { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import { toast } from 'react-toastify';
import TabInputData from './TabInputData'; // Komponent do dodawania nowych danych

Modal.setAppElement('#root');  // Ustawienie aplikacji jako root dla modala

const tdStyle = {
  padding: '10px',
  verticalAlign: 'top',
  borderBottom: '1px solid #eee',
  fontSize: '14px'
};

const deleteBtn = {
  backgroundColor: '#dc3545',
  color: '#fff',
  border: 'none',
  borderRadius: '6px',
  padding: '4px 8px',
  cursor: 'pointer'
};

const labelStyle = {
  display: 'block',
  textAlign: 'left',
  fontWeight: 600,
  marginBottom: 4,
  marginLeft: 3
};

const inputStyle = {
  width: '100%',
  padding: '10px',
  borderRadius: '8px',
  border: '1px solid #ccc',
  boxSizing: 'border-box',
  outlineColor: '#007bff'
};

function TabViewData() {
  const [entries, setEntries] = useState([]);  // Lista danych
  const [sortedEntries, setSortedEntries] = useState([]);  // Posortowane dane
  const [editingEntry, setEditingEntry] = useState(null);  // Stan edytowanego wpisu
  const [editForm, setEditForm] = useState({});  // Formularz edycji
  const [isAdding, setIsAdding] = useState(false);  // Widoczno≈õƒá modala dodawania
  const [sortOrder, setSortOrder] = useState('asc');  // Kierunek sortowania (rosnƒÖco/malejƒÖco)
  const [sortColumn, setSortColumn] = useState('nazwisko');  // Kolumna do sortowania

  // Normalizowanie danych przed zapisaniem
  const normalizeEntryData = (entry) => {
    return {
      ...entry,
      imie: entry.imie, // Normalize imie
      nazwisko: entry.nazwisko, // Normalize nazwisko
      jezyk: entry.jezyk, // Normalize jezyk
      fs: entry.fs,
      nr: entry.nr,
      do_opieki: entry.do_opieki,
      dyspozycyjnosc: entry.dyspozycyjnosc,
      oczekiwania: entry.oczekiwania,
      referencje: entry.referencje,
      ostatni_kontakt: entry.ostatni_kontakt,
      notatka: entry.notatka
    }
  };

  // Pobieranie danych z serwera
  const fetchEntries = () => {
    axios.get('/entries')
      .then(res => {
        const normalizedData = res.data.map(normalizeEntryData);
        setEntries(normalizedData);
        setSortedEntries(normalizedData);  // Domy≈õlnie wszystkie dane
      })
      .catch(error => console.error('B≈ÇƒÖd pobierania danych:', error));
  };

  useEffect(() => {
    fetchEntries();  // Pobieramy dane po za≈Çadowaniu komponentu
  }, []);
  const handleSort = (column) => {
    let newSortOrder = 'asc';  // Domy≈õlnie ustawiamy sortowanie rosnƒÖce
  
    // Zmiana kierunku sortowania po klikniƒôciu w tƒô samƒÖ kolumnƒô
    if (sortColumn === column) {
      newSortOrder = sortOrder === 'asc' ? 'desc' : 'asc'; // Je≈õli klikamy na tƒô samƒÖ kolumnƒô, zmieniamy kierunek
    }
  
    console.log('Dane przed sortowaniem:', entries);  // Dodajemy pe≈Çne logowanie, by sprawdziƒá dane
    console.log('Kolumna sortowania:', columnKey); // Wy≈õwietlanie warto≈õci columnKey w konsoli
  
    const sortedData = [...entries].sort((a, b) => {
      let valueA = a[column];  // Otrzymujemy warto≈õƒá z kolumny
      let valueB = b[column];  // Otrzymujemy warto≈õƒá z kolumny
  
      // Logowanie por√≥wnywanych danych
      console.log(`Por√≥wnywane dane: ${column} | valueA: ${valueA}, valueB: ${valueB}`);
  
      // Sprawdzamy, czy warto≈õci sƒÖ puste
      if (valueA === undefined || valueB === undefined) {
        console.error(`B≈ÇƒÖd: Brak warto≈õci w kolumnie ${column}`);
      }
  
      // Je≈õli dane sƒÖ liczbowe, przekszta≈Çcamy je na liczby
      if (!isNaN(valueA) && !isNaN(valueB)) {
        valueA = parseFloat(valueA);
        valueB = parseFloat(valueB);
      }
  
      // Je≈õli to ciƒÖgi tekstowe, u≈ºywamy localeCompare
      if (typeof valueA === 'string' && typeof valueB === 'string') {
        return valueA.localeCompare(valueB, 'pl', { sensitivity: 'base' }); // Por√≥wnanie tekstowe
      }
  
      // Je≈õli to liczby, por√≥wnujemy je bezpo≈õrednio
      return valueA - valueB;
    });
  
    setSortedEntries(sortedData);
    setSortOrder(newSortOrder); // Zmiana kierunku sortowania
    setSortColumn(column); // Ustawienie kolumny do sortowania
  };
  // Funkcja do usuwania wpisu
  const handleDelete = async (id, e) => {
    e.stopPropagation();  // Zapobieganie wywo≈Çaniu funkcji edycji po klikniƒôciu kosza
    if (!window.confirm('Czy na pewno chcesz usunƒÖƒá ten wpis?')) return;
    try {
      await axios.delete(`/entries/${id}`);
      toast.success('Wpis usuniƒôty');
      fetchEntries();  // Od≈õwie≈ºenie listy wpis√≥w
    } catch {
      toast.error('B≈ÇƒÖd podczas usuwania');
    }
  };

  // Funkcja otwierania formularza edycji
  const handleEdit = (entry) => {
    setEditingEntry(entry);
    setEditForm({
      ...entry,
      dyspozycyjnosc: formatDate(entry.dyspozycyjnosc),
      ostatni_kontakt: formatDate(entry.ostatni_kontakt),
    });
  };

  // Funkcja do zmiany danych w formularzu edycji
  const handleEditChange = (field, value) => {
    setEditForm((prev) => ({ ...prev, [field]: value }));
  };

  // Funkcja do zapisania edytowanego wpisu
  const handleSaveEdit = async () => {
    try {
      await axios.put(`/entries/${editingEntry.id}`, editForm);
      toast.success('Zaktualizowano dane');
      setEditingEntry(null);  // Zamkniƒôcie formularza po zapisaniu
      fetchEntries();  // Od≈õwie≈ºenie danych
    } catch {
      toast.error('B≈ÇƒÖd edycji');
    }
  };

  return (
    <div style={{ overflowX: 'auto', padding: 20 }}>
      <h2 style={{ textAlign: 'center' }}>üìÑ Wpisy w bazie danych</h2>

      <button
        onClick={() => setIsAdding(true)}
        style={{
          marginBottom: 20,
          padding: '10px 15px',
          backgroundColor: '#28a745',
          color: '#fff',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer',
          fontSize: '20px',
          fontWeight: 'bold',
          position: 'absolute',
          right: '20px',
          top: '20px'
        }}
      >
        +
      </button>

      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead style={{ backgroundColor: '#007bff' }}>
          <tr>
            {['#', 'Imiƒô', 'Nazwisko', 'Jƒôzyk', 'FS', 'NR', 'Do opieki', 'Dyspozycyjno≈õƒá', 'Oczekiwania', 'Referencje', 'Ostatni kontakt', 'Notatka'].map((col, i) => {
              const columnKey = col.toLowerCase().replace(' ', '_');  // Przyk≈Çad: 'Imiƒô' -> 'imie'
              return (
                <th
                  key={i}
                  style={{
                    padding: '10px',
                    color: '#fff',
                    fontWeight: '600',
                    textAlign: 'left',
                    borderBottom: '1px solid #e0e0e0',
                    cursor: 'pointer'
                  }}
                  onClick={() => handleSort(columnKey) && console.log({columnKey})}  // Przekazujemy ma≈ÇƒÖ wersjƒô nazwy kolumny
                >
                  {col}
                  {sortColumn === columnKey && (sortOrder === 'asc' ? ' üîº' : ' üîΩ')}
                </th>
              );
            })}
          </tr>
        </thead>
        <tbody>
          {sortedEntries.map((entry, index) => (
            <tr key={entry.id}>
              <td>{index + 1}</td>
              <td>{entry.imie}</td>
              <td>{entry.nazwisko}</td>
              <td>{entry.jezyk}</td>
              <td>{entry.fs}</td>
              <td>{entry.nr}</td>
              <td>{entry.do_opieki}</td>
              <td>{entry.dyspozycyjnosc}</td>
              <td>{entry.oczekiwania}</td>
              <td>{entry.referencje}</td>
              <td>{entry.ostatni_kontakt}</td>
              <td>{entry.notatka}</td>
              <td>
                <button onClick={(e) => handleDelete(entry.id, e)} style={deleteBtn}>üóëÔ∏è</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal do edytowania wpisu */}
      <Modal
        isOpen={!!editingEntry}
        onRequestClose={() => setEditingEntry(null)}
        contentLabel="Edytuj wpis"
        style={{
          content: {
            maxWidth: '600px',
            margin: 'auto',
            padding: '30px',
            borderRadius: '12px',
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
            background: '#fff'
          }
        }}
      >
        <button
          onClick={() => setEditingEntry(null)}
          style={{
            position: 'absolute',
            top: 10,
            right: 15,
            background: 'transparent',
            border: 'none',
            fontSize: 20,
            fontWeight: 'bold',
            cursor: 'pointer',
            color: '#999'
          }}
        >
          &times;
        </button>

        <h2 style={{ textAlign: 'center', marginBottom: 20 }}>‚úèÔ∏è Edycja wpisu</h2>
        {editingEntry && (
          <form onSubmit={(e) => { e.preventDefault(); handleSaveEdit(); }}>
            {/* Formularz edycji */}
            <div style={{ display: 'flex', gap: '10px', marginBottom: 16 }}>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>Imiƒô</label>
                <input type="text" value={editForm.imie || ''} onChange={e => handleEditChange('imie', e.target.value)} style={inputStyle} />
              </div>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>Nazwisko</label>
                <input type="text" value={editForm.nazwisko || ''} onChange={e => handleEditChange('nazwisko', e.target.value)} style={inputStyle} />
              </div>
            </div>
            {/* Jƒôzyk + FS + NR */}
            <div style={{ display: 'flex', gap: '10px', marginBottom: 16 }}>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>Jƒôzyk</label>
                <select value={editForm.jezyk || ''} onChange={e => handleEditChange('jezyk', e.target.value)} style={inputStyle}>
                  <option value="">Wybierz</option>
                  {['A0', 'A1', 'A2', 'B1', 'B2', 'C1'].map(lvl => <option key={lvl} value={lvl}>{lvl}</option>)}
                </select>
              </div>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>FS</label>
                <select value={editForm.fs || ''} onChange={e => handleEditChange('fs', e.target.value)} style={inputStyle}>
                  <option value="">Wybierz</option>
                  <option value="Tak">Tak</option>
                  <option value="Nie">Nie</option>
                </select>
              </div>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>NR</label>
                <select value={editForm.nr || ''} onChange={e => handleEditChange('nr', e.target.value)} style={inputStyle}>
                  <option value="">Wybierz</option>
                  <option value="Tak">Tak</option>
                  <option value="Nie">Nie</option>
                </select>
              </div>
            </div>

            {/* Do opieki + Dyspozycyjno≈õƒá */}
            <div style={{ display: 'flex', gap: '10px', marginBottom: 16 }}>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>Do opieki</label>
                <select value={editForm.do_opieki || ''} onChange={e => handleEditChange('do_opieki', e.target.value)} style={inputStyle}>
                  <option value="">Wybierz</option>
                  <option value="senior">Senior</option>
                  <option value="seniorka">Seniorka</option>
                  <option value="ma≈Ç≈ºe≈Ñstwo">Ma≈Ç≈ºe≈Ñstwo</option>
                </select>
              </div>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>Dyspozycyjno≈õƒá</label>
                <input type="month" value={editForm.dyspozycyjnosc || ''} onChange={e => handleEditChange('dyspozycyjnosc', e.target.value)} style={inputStyle} />
              </div>
            </div>

            {/* Oczekiwania */}
            <div style={{ marginBottom: 16 }}>
              <label style={labelStyle}>Oczekiwania</label>
              <textarea value={editForm.oczekiwania || ''} onChange={e => handleEditChange('oczekiwania', e.target.value)} rows={3} style={inputStyle} />
            </div>

            {/* Referencje + ostatni kontakt */}
            <div style={{ display: 'flex', gap: '10px', marginBottom: 16 }}>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>Referencje</label>
                <select value={editForm.referencje || ''} onChange={e => handleEditChange('referencje', e.target.value)} style={inputStyle}>
                  <option value="">Wybierz</option>
                  <option value="Tak">Tak</option>
                  <option value="Nie">Nie</option>
                </select>
              </div>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>Ostatni kontakt</label>
                <input type="date" value={editForm.ostatni_kontakt || ''} onChange={e => handleEditChange('ostatni_kontakt', e.target.value)} style={inputStyle} />
              </div>
            </div>

            {/* Notatka */}
            <div style={{ marginBottom: 16 }}>
              <label style={{ display: 'block', textAlign: 'center', fontWeight: 600, marginBottom: 4 }}>Notatka</label>
              <textarea value={editForm.notatka || ''} onChange={e => handleEditChange('notatka', e.target.value)} rows={3} style={inputStyle} />
            </div>
            <button type="submit" style={{
              width: '100%',
              padding: '12px',
              backgroundColor: '#007bff',
              color: '#fff',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer'
            }}>
              üíæ Zapisz zmiany
            </button>
          </form>
        )}
      </Modal>

      {/* Modal do dodawania nowych danych */}
      <Modal
        isOpen={isAdding}
        onRequestClose={() => setIsAdding(false)}
        contentLabel="Dodaj nowy wpis"
        style={{
          content: {
            maxWidth: '600px',
            margin: 'auto',
            padding: '30px',
            borderRadius: '12px',
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
            background: '#fff'
          }
        }}
      >
        <button
          onClick={() => setIsAdding(false)}  // Zamkniƒôcie modala
          style={{
            position: 'absolute',
            top: 10,
            right: 15,
            background: 'transparent',
            border: 'none',
            fontSize: 20,
            fontWeight: 'bold',
            cursor: 'pointer',
            color: '#999'
          }}
        >
          &times;
        </button>
        <h2 style={{ textAlign: 'center', marginBottom: 20 }}>+ Dodaj nowy wpis</h2>
        <TabInputData setIsAdding={setIsAdding} fetchEntries={fetchEntries} />
      </Modal>
    </div>
  );
}

export default TabViewData;
