import { useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';

function ForgotPassword() {
  const [email, setEmail] = useState('');
  const [newPassword, setNewPassword] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:3001/reset-password', { email });
      setNewPassword(res.data.newPassword);
      toast.success('Nowe hasło wygenerowane!');
    } catch {
      toast.error('Nie znaleziono użytkownika');
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Odzyskiwanie hasła</h2>
      <form onSubmit={handleSubmit}>
        <input placeholder="Twój email" value={email} onChange={e => setEmail(e.target.value)} required />
        <button type="submit">Resetuj hasło</button>
      </form>
      {newPassword && (
        <div style={{ marginTop: 10 }}>
          <strong>Nowe hasło:</strong> {newPassword}
        </div>
      )}
      <p style={{ marginTop: 10 }}>
        <span style={{ color: 'blue', cursor: 'pointer' }} onClick={() => navigate('/login')}>Powrót do logowania</span>
      </p>
    </div>
  );
}
export default ForgotPassword;
