// Pagination.js
import React from "react";
import styled from "styled-components";
import { Link } from "../../routes";
import colors from "../../consts/colors";

const Flex = styled.div`
  display: flex;
  justify-content: flex-end;
  align-items: center;
`;

const PaginationLink = styled.a`
  display: flex;
  font-family: Lato, sans-serif;
  font-size: 11px;
  font-weight: bold;
  line-height: 2.18;
  text-align: left;
  text-decoration: none;
  width: 22px;
  height: 22px;
  border-radius: 3px;
  background-color: ${colors.wildBlue};
  align-items: center;
  justify-content: center;
  margin: 0 2px;
  color: ${(props) => (props.active ? colors.white : colors.wildBlue)};
  background: ${(props) => (props.active ? colors.wildBlue : colors.white)};
  cursor: pointer;

  :hover {
    background: ${colors.wildBlue};
    color: ${colors.white};
  }
`;

const NextPreviousLink = styled.a`
  font-size: 11px;
  font-weight: bold;
  text-align: center;
  border-radius: 3px;
  cursor: pointer;
  height: 22px;
  min-width: 70px;
  display: flex;
  background-color: #f0f2f9;
  color: ${colors.wildBlue};
  text-decoration: none;

  :hover {
    color: ${colors.white};
    background: ${colors.wildBlue};
  }
`;

const PreviousLink = styled(NextPreviousLink)`
  margin-right: 8px;
`;

const NextLink = styled(NextPreviousLink)`
  margin-left: 8px;
`;

const Pagination = ({ pages, currentPage, onPageChange }) => {
  const current = Number(currentPage);
  return (
    <Flex>
      {current > 1 && (
        <button onClick={() => onPageChange(current - 1)}>
          Poprzedni
        </button>
      )}
      {Array.from({ length: pages }).map((_, idx) => {
        const pageNum = idx + 1;
        return (
          <button
            key={pageNum}
            onClick={() => onPageChange(pageNum)}
            style={{
              margin: "0 2px",
              padding: "4px 8px",
              background: current === pageNum ? colors.wildBlue : colors.white,
              color: current === pageNum ? colors.white : colors.wildBlue,
              border: "1px solid " + colors.wildBlue,
              borderRadius: "3px",
              cursor: "pointer",
            }}
          >
            {pageNum}
          </button>
        );
      })}
      {current < pages && (
        <button onClick={() => onPageChange(current + 1)}>
          NastÄ™pny
        </button>
      )}
    </Flex>
  );
};

export default Pagination;
