import { useEffect, useState } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import { toast } from 'react-toastify';

Modal.setAppElement('#root');

const tdStyle = {
  padding: '10px',
  verticalAlign: 'top',
  borderBottom: '1px solid #eee',
  fontSize: '14px'
};

const editBtn = {
  backgroundColor: '#ffc107',
  color: '#000',
  border: 'none',
  borderRadius: '6px',
  padding: '4px 8px',
  cursor: 'pointer'
};

const deleteBtn = {
  backgroundColor: '#dc3545',
  color: '#fff',
  border: 'none',
  borderRadius: '6px',
  padding: '4px 8px',
  cursor: 'pointer'
};

const labelStyle = {
  display: 'block',
  textAlign: 'left',
  fontWeight: 600,
  marginBottom: 4,
  marginLeft: 3
};

const inputStyle = {
  width: '100%',
  padding: '10px',
  borderRadius: '8px',
  border: '1px solid #ccc',
  boxSizing: 'border-box',
  outlineColor: '#007bff'
};

function TabViewData() {
  const [entries, setEntries] = useState([]);
  const [editingEntry, setEditingEntry] = useState(null);
  const [editForm, setEditForm] = useState({});
  const [isAdding, setIsAdding] = useState(false);

  const fetchEntries = () => {
    axios.get('/entries').then(res => setEntries(res.data));
  };

  useEffect(() => {
    fetchEntries();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Czy na pewno chcesz usunƒÖƒá ten wpis?')) return;
    try {
      await axios.delete(`/entries/${id}`);
      toast.success('Wpis usuniƒôty');
      fetchEntries();
    } catch {
      toast.error('B≈ÇƒÖd podczas usuwania');
    }
  };
  const handleEdit = (entry) => {
    console.log('Edytowany wpis:', entry);  // Dodajemy logowanie danych
    setEditingEntry(entry);
    setEditForm({ ...entry });
  };

  const handleEditChange = (field, value) => {
    setEditForm((prev) => ({ ...prev, [field]: value }));
  };

  const handleSaveEdit = async () => {
    try {
      await axios.put(`http://localhost:3001/entries/${editingEntry.id}`, editForm);
      toast.success('Zaktualizowano dane');
      setEditingEntry(null);
      fetchEntries();
    } catch {
      toast.error('B≈ÇƒÖd edycji');
    }
  };

  const handleAddEntry = async (form) => {
    try {
      await axios.post('http://localhost:3001/entries', form);
      toast.success('Wpis dodany');
      setIsAdding(false);
      fetchEntries();
    } catch {
      toast.error('B≈ÇƒÖd przy dodawaniu wpisu');
    }
  };

  return (
    <div style={{ overflowX: 'auto', padding: 20 }}>
      <button
        onClick={() => setIsAdding(true)}
        style={{
          marginBottom: 20,
          padding: '10px 15px',
          backgroundColor: '#28a745',
          color: '#fff',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer',
          fontSize: '20px',
          fontWeight: 'bold',
          position: 'absolute',
          right: '20px',
          top: '20px'
        }}
      >
        +
      </button>
      <h2 style={{ textAlign: 'center' }}>üìÑ Wpisy w bazie danych</h2>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead style={{ backgroundColor: '#007bff' }}>
          <tr>
            {[
              '#', 'Imiƒô', 'Nazwisko', 'Jƒôzyk', 'FS', 'NR', 'Do opieki', 'Dyspozycyjno≈õƒá',
              'Oczekiwania', 'Referencje', 'Ostatni kontakt', 'Notatka', '‚úèÔ∏è', 'üóëÔ∏è'
            ].map((col, i) => (
              <th key={i} style={{
                padding: '10px',
                color: '#fff',
                fontWeight: '600',
                textAlign: 'left',
                borderBottom: '1px solid #e0e0e0'
              }}>{col}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {entries.map((entry, index) => (
            <tr
              key={entry.id}
              onClick={() => handleEdit(entry)}
              style={{
                borderBottom: '1px solid #eee',
                transition: 'background-color 0.2s',
                cursor: 'pointer'
              }}
              onMouseEnter={e => e.currentTarget.style.backgroundColor = '#f5f9ff'}
              onMouseLeave={e => e.currentTarget.style.backgroundColor = ''}
            >
              <td style={tdStyle}>{index + 1}</td>
              <td style={tdStyle}>{entry.imie}</td>
              <td style={tdStyle}>{entry.nazwisko}</td>
              <td style={tdStyle}>{entry.jezyk}</td>
              <td style={tdStyle}>{entry.fs}</td>
              <td style={tdStyle}>{entry.nr}</td>
              <td style={tdStyle}>{entry.do_opieki}</td>
              <td style={tdStyle}>{entry.dyspozycyjnosc}</td>
              <td style={tdStyle}>{entry.oczekiwania}</td>
              <td style={tdStyle}>{entry.referencje}</td>
              <td style={tdStyle}>
                {entry.ostatni_kontakt
                  ? new Date(entry.ostatni_kontakt).toLocaleDateString('pl-PL')
                  : '‚Äî'}
              </td>
              <td style={tdStyle}>{entry.notatka}</td>
              <td style={tdStyle}>
                <button onClick={() => handleEdit(entry)} style={editBtn}>‚úèÔ∏è</button>
              </td>
              <td style={tdStyle}>
                <button onClick={() => handleDelete(entry.id)} style={deleteBtn}>üóëÔ∏è</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* MODAL */}
      <Modal
        isOpen={!!editingEntry}
        onRequestClose={() => setEditingEntry(null)}
        contentLabel="Edytuj wpis"
        style={{
          content: {
            maxWidth: '600px',
            margin: 'auto',
            padding: '30px',
            borderRadius: '12px',
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
            background: '#fff'
          }
        }}
      >
        <h2 style={{ textAlign: 'center', marginBottom: 20 }}>‚úèÔ∏è Edycja wpisu</h2>
        {editingEntry && (
          <form onSubmit={(e) => { e.preventDefault(); handleSaveEdit(); }}>
            <div style={{ display: 'flex', gap: '10px', marginBottom: 16 }}>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>Imiƒô</label>
                <input type="text" value={editForm.imie || ''} onChange={e => handleEditChange('imie', e.target.value)} style={inputStyle} />
              </div>
              <div style={{ flex: 1 }}>
                <label style={labelStyle}>Nazwisko</label>
                <input type="text" value={editForm.nazwisko || ''} onChange={e => handleEditChange('nazwisko', e.target.value)} style={inputStyle} />
              </div>
            </div>
            {/* Pozosta≈Çe pola... */}
            <button type="submit" style={{
              width: '100%',
              padding: '12px',
              backgroundColor: '#007bff',
              color: '#fff',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer'
            }}>
              üíæ Zapisz zmiany
            </button>
          </form>
        )}
      </Modal>
    </div>
  );
}

export default TabViewData;
