// Offers.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const OffersTable = () => {
  const [offers, setOffers] = useState([]); // wszystkie oferty (bez paginacji)
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const pageSize = 10; // liczba ofert na stronę (używana do pobierania wszystkich stron)

  useEffect(() => {
    const fetchAllOffers = async () => {
      setLoading(true);
      try {
        // Pobieramy stronę 1, aby uzyskać metadane (meta.all) i pierwsze wyniki
        const firstResponse = await axios.get('https://api-lockstep.berlin-opiekunki.pl/v1/offer/all', {
          params: { page: 1, pageSize }
        });
        const { meta, offers: firstPageOffers } = firstResponse.data;
        const totalOffersCount = meta.all; // np. 21 ofert
        const totalPagesAPI = Math.ceil(totalOffersCount / pageSize); // np. 3 strony

        let allOffers = firstPageOffers;
        // Jeśli API ma więcej niż jedną stronę, pobieramy pozostałe strony równolegle
        if (totalPagesAPI > 1) {
          const requests = [];
          for (let page = 2; page <= totalPagesAPI; page++) {
            requests.push(
              axios.get('https://api-lockstep.berlin-opiekunki.pl/v1/offer/all', {
                params: { page, pageSize }
              })
            );
          }
          const responses = await Promise.all(requests);
          responses.forEach(response => {
            const offersPage = response.data.offers || [];
            allOffers = allOffers.concat(offersPage);
          });
        }
        // Usuń duplikaty – zakładamy, że każda oferta ma unikalne pole "id"
        allOffers = [...new Map(allOffers.map(offer => [offer.id, offer])).values()];
        // Przytnij wyniki do łącznej liczby ofert (np. 21)
        allOffers = allOffers.slice(0, totalOffersCount);
        // Sortowanie wszystkich ofert według daty rozpoczęcia (rosnąco)
        const sortedOffers = allOffers.sort((a, b) => new Date(a.startDate) - new Date(b.startDate));
        setOffers(sortedOffers);
      } catch (err) {
        console.error('Błąd pobierania ofert:', err);
        setError('Błąd pobierania ofert');
      } finally {
        setLoading(false);
      }
    };

    fetchAllOffers();
  }, []);

  if (loading) return <div>Ładowanie ofert...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h2 style={{ textAlign: 'center' }}>Oferty Pracy</h2>
      <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
        <thead>
          <tr style={{ backgroundColor: '#f4f4f4' }}>
            <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'left' }}>ID</th>
            <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'left' }}>Tytuł</th>
            <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'left' }}>Opis</th>
            <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'left' }}>Miasto</th>
            <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'left' }}>Kod</th>
            <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'left' }}>Płeć</th>
            <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'left' }}>Data rozpoczęcia</th>
          </tr>
        </thead>
        <tbody>
          {offers.map((offer, index) => (
            <tr key={offer.id || index}>
              <td style={{ border: '1px solid #ddd', padding: '12px' }}>{offer.id}</td>
              <td style={{ border: '1px solid #ddd', padding: '12px' }}>{offer.title}</td>
              <td style={{ border: '1px solid #ddd', padding: '12px' }}>{offer.description}</td>
              <td style={{ border: '1px solid #ddd', padding: '12px' }}>{offer.city}</td>
              <td style={{ border: '1px solid #ddd', padding: '12px' }}>{offer.code}</td>
              <td style={{ border: '1px solid #ddd', padding: '12px' }}>{offer.sex}</td>
              <td style={{ border: '1px solid #ddd', padding: '12px' }}>
                {offer.startDate ? new Date(offer.startDate).toLocaleString() : 'Brak daty'}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OffersTable;
